import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class Main {public static void main(String[] args) throws NumberFormatException, IOException {BufferedReader br = new BufferedReader(new InputStreamReader(System.in));int n = Integer.parseInt(br.readLine()), total = 0;char[] l1 = br.readLine().toCharArray(), l2 = br.readLine().toCharArray();for(int i = 0; i < n; i++) {if(l1[i] == 'C' && l2[i] == 'C') {total++;}}System.out.println(total);}}